    int size = 1;
    for (int i = 0; i < n; ++i)
    {
        size *= 3;
    }

    vector<int> res(size);

    rec(0, 1, size, sequence, res);

    for (auto x : res)
    {
        cout << x << " ";
    }

    cout << endl;

    return 0;
}

