int fast_pow(int a, int b)
{
	int ret(1);
	for (int p(a); b; p *= p, b /= 2)
		if (b%2)
			ret *= p;
	return ret;
}

int main(){
    int N,p,size,len,tmp1,tmp2,cou=3,sw;
    string T;
    cin >> N;
    cin >> T;
    size = fast_pow(3,N);
    vector<int> table(size);
    len = T.length();
    table[0] = 0;
    for(int i=1;i<size;i++){
        if(i % 3 == 0){
            if(i == cou){
                sw = 1;
                table[i] = cou * 2;
                tmp1 = cou;
                tmp2 = cou*2;
                cou *= 3;
            }else if(i == tmp2){
                table[i] = tmp1;
                sw = 2;
            }else{
                if(sw == 1){
                    table[i] = table[i-tmp1] + tmp2;
                }else{
                    table[i] = table[i-tmp2] + tmp1;
                }
            }
        }else if(i % 3 == 1){
            table[i] = table[i-1] + 2;
        }else{
            table[i] = table[i-1] - 1;
        }
    }
