    }
    cout << '\n'; 
}

int get(int x) {
    vector<int> bts; 
    for (int i = 0; i < L; ++i) {
        bts.pb(x % 3); 
        x /= 3; 
    }
    int left = 0;
    int right = 0; 
    for (int i = 6; i >= 0; --i) {
        left *= 3;
        left += bts[i]; 
    }
    for (int i = L - 1; i >= 7; --i) {
        right *= 3; 
        right += bts[i]; 
    }
    int id = 0;         
    if (left && dp1[left] == -1) {
        if ((pref[(int)s.size()] - pref[id]) & 1) {        
            right = xyz[right];     
        }
        return res[left] + add * right;   
    }
    if (left) {    
        id = dp1[left]; 
//        print(right); 
        if (pref[id] & 1) {
            right = xyz[right];             
        }
        ++right;
//        print(right); 
        left = 0; 
    }                     
    while (next[id] != -1) {        
        if ((pref[next[id]] - pref[id]) & 1) {
            right = xyz[right]; 
        }
        right++; 
        id = next[id];                                                                                                                                         
    }
    if ((pref[(int)s.size()] - pref[id]) & 1) {        
        right = xyz[right];     
    }
    left = to[id]; 
    return left + right * add; 
}

