int salsa(int n,int a)
{
    int list[n];
    for(int i=0;i<n;i++)
    {
        list[i]=0;
    }
    int j=n-1;
    while(a!=0)
    {
        list[j]= a % 3 ;
        a=a/3;
        j--;
    }
    for(int i=0;i<n;i++)
    {
        if(list[i]==1)
        {
            list[i]=2;
        }
        else if(list[i]==2)
        {
            list[i]=1;
        }
    }
    int result=0;
    for(int i=0;i<n;i++)
    {
        result=result+ list[i]*pow(3,(n-1-i));
    }
    return result;
}

