int salsa(int p)
{
  int r=0;
  int q=0;
  int a;
  while(p>0)
  {
    a=p%3;
    if(a==1) r+=2*(int)pow(3,q);
    if(a==2) r+=(int)pow(3,q);
    p/=3;
    q++;
  }
  return r;
}

