int convert(int x, int n){
    int y=0, i=0, z;
    while(x > 0){
        z = x%n;
        if(z == 1){
          z = 2;
        }else if(z==2){
          z = 1;
        }
        y += z*pow(n, i);
        x = x/n;
        i++;
    }
    return y;
}

