                }
            }
            for(int i=0;i<circle.length;i++){
                if(i==circle.length){
                    System.out.println(circle[i]);
                }else {
                    System.out.print(circle[i] + " ");
                }
            }
        }catch (IOException e){
            e.printStackTrace();;
        }
    }
}

