import java.util.*;

public class Main {
	public static void main (String[] args) {
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int length = (int)(Math.pow(3, n));
		Person.size = n;
		Person[] persons = new Person[length];
		for (int i = 0; i < length; i++) {
		    persons[i] = new Person(i);
		}
		ArrayList<Integer> list = new ArrayList<>();
		char[] arr = sc.next().toCharArray();
		int count = 0;
		for (int i = 0; i < arr.length; i++) {
		    if (arr[i] == 'R') {
		        count++;
		    } else {
		        if (i < arr.length - 1 && arr[i + 1] == 'S') {
		            i++;
		        } else {
		            list.add(count);
		            count = 0;
		        }
		    }
		}
		list.add(count);
		Person.list = list;
		StringBuilder sb = new StringBuilder();
		for (int i = 0; i < length; i++) {
		    if (i > 0) {
		        sb.append(" ");
		    }
		    persons[i].calc();
		    sb.append(persons[i].getValue());
		}
		System.out.println(sb);
	}

	static class Person {
	    static int size;
	    static ArrayList<Integer> list;
	    int[] base;

	    public Person(int value) {
	        base = new int[size];
	        for (int i = 0; i < size && value > 0; i++) {
	            base[i] = value % 3;
	            value /= 3;
	        }
	    }

	    public void calc() {
	        int idx = 0;
	        while (list.size() - idx > 2) {
	            int first = list.get(idx);
	            int second = list.get(idx + 1);
	            for (int i = 0; i < size && (first > 0 || second > 0); i++) {
	                int modF = first % 3;
	                int modS = second % 3;
	                first /= 3;
	                second /= 3;
	                if (modF == 0 && modS == 0) {
	                    continue;
	                }
	                if (base[i] + modF >= 3) {
	                    first++;
	                }
	                base[i] = (base[i] + modF) % 3;
	                if (base[i] == 1) {
	                    base[i] = 2;
	                } else if (base[i] == 2) {
	                    base[i] = 1;
	                }
	                if (base[i] + modS >= 3) {
	                    second++;
	                }
	                base[i] = (base[i] + modS) % 3;
	                if (base[i] == 1) {
	                    base[i] = 2;
	                } else if (base[i] == 2) {
	                    base[i] = 1;
	                }
	            }
	            idx += 2;
	        }
	        if (list.size() - idx == 2) {
	            int x = list.get(idx);
	            for (int i = 0; i < size; i++) {
	                if (x > 0) {
    	                int mod = x % 3;
    	                x /= 3;
                        if (base[i] + mod >= 3) {
                            x++;
                        }
                        base[i] = (base[i] + mod) % 3;
	                }
	                if (base[i] == 1) {
	                    base[i] = 2;
	                } else if (base[i] == 2) {
	                    base[i] = 1;
	                }
	            }
	            idx++;
	        }
            int x = list.get(idx);
            for (int i = 0; i < size && x > 0; i++) {
                int mod = x % 3;
                x /= 3;
                if (base[i] + mod >= 3) {
                    x++;
                }
                base[i] = (base[i] + mod) % 3;
            }
 	    }

	    public int getValue() {
	        int ans = 0;
	        for (int i = size - 1; i >= 0; i--) {
	            ans *= 3;
	            ans += base[i];
	        }
	        return ans;
	    }
	}
}

