    int arr[]=new int[n];
    int seni[]=new int[n];
    int len = t.length();
    for(int i=0;i<n;i++){
      arr[i]=i;
      if(i!=0&&seni[i]==0){
        seni[i] = trans(i);
        seni[seni[i]]=i;
      }
    }
    for(int i=0;i<len;i++){
      char c = t.charAt(i);
      if(c=='S'){
        boolean flg[]=new boolean[n];
        for(int j=0;j<n;j++){
          if(!flg[j]){
            int x = arr[j];
            arr[j]=arr[seni[j]];
            arr[seni[j]]=x;
            flg[j]=true;flg[seni[j]]=true;
          }
        }
      }else{
        int x = arr[n-1];
        for(int j=n-1;j>0;j--){
          arr[j]=arr[j-1];
        }
        arr[0]=x;
      }
    }
    int ans[] = new int[n];
    for(int i=0;i<n;i++){
      ans[arr[i]]=i;
    }
    pr(ans[0]+"");
    for(int i=1;i<n;i++){
      pr(" "+ans[i]);
    }
    pl("");

