  public static int trans(int n){
    int k,r=0;
    int bai=1;
    while(n!=0){
      k = n%3;
      n=n/3;
      if(k==1){
        k=2;
      }else if(k==2){
        k=1;
      }
      r += k*bai;
      bai*=3;
    }
    return r;
  }

