import static java.lang.Integer.parseInt;
import static java.lang.Long.parseLong;
import static java.lang.System.arraycopy;
import static java.lang.System.exit;

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.HashMap;
import java.util.Map;
import java.util.StringTokenizer;

public class Main {

	static Map<Long, int[]> cache[];
	static int nShifts;
	static int shiftsC[][];
	static int p3[];

	static int[] solve(int n) {
		int curShifts[] = shiftsC[n];
		long k = 1;
		for (int i = 0; i <= nShifts; i++) {
			k = (k * 239 + curShifts[i]) % 1000000009;
		}
		Long key = k;
		int res[] = cache[n].get(key);
		if (res == null) {
			if (n == 0) {
				res = new int[1];
			} else {
				res = new int[p3[n]];
				int nextShifts[] = shiftsC[n - 1];
				for (int sd = 0; sd < 3; sd++) {
					int d = sd;
					for (int i = 0;; i++) {
						int x = d + curShifts[i];
						nextShifts[i] = x / 3;
						d = x % 3;
						if (i == nShifts) {
							break;
						}
						if (d != 0) {
							d = 3 - d;
						}
					}
					int nextRes[] = solve(n - 1);
					for (int i = 0; i < nextRes.length; i++) {
						res[3 * i + sd] = 3 * nextRes[i] + d;
					}
				}
			}
			cache[n].put(key, res);
		}
		return res;
	}

	static void solve() throws Exception {
		int n = scanInt();
		cache = new Map[n + 1];
		for (int i = 0; i <= n; i++) {
			cache[i] = new HashMap<>();
		}
		String s = scanString();
		int shifts[] = new int[s.length() + 1];
		nShifts = 0;
		for (int i = 0; i < s.length(); i++) {
			if (s.charAt(i) == 'R') {
				++shifts[nShifts];
			} else {
				if (nShifts > 0 && shifts[nShifts] == 0) {
					--nShifts;
				} else {
					++nShifts;
				}
			}
		}
		shiftsC = new int[n + 1][nShifts + 1];
		arraycopy(shifts, 0, shiftsC[n], 0, nShifts + 1);
		p3 = new int[n + 1];
		p3[0] = 1;
		for (int i = 1; i <= n; i++) {
			p3[i] = p3[i - 1] * 3;
		}
		int res[] = solve(n);
		for (int i = 0; i < res.length; i++) {
			out.print(res[i] + " ");
		}
	}

	static int scanInt() throws IOException {
		return parseInt(scanString());
	}

	static long scanLong() throws IOException {
		return parseLong(scanString());
	}

	static String scanString() throws IOException {
		while (tok == null || !tok.hasMoreTokens()) {
			tok = new StringTokenizer(in.readLine());
		}
		return tok.nextToken();
	}

	static BufferedReader in;
	static PrintWriter out;
	static StringTokenizer tok;

	public static void main(String[] args) {
		try {
			in = new BufferedReader(new InputStreamReader(System.in));
			out = new PrintWriter(System.out);
			solve();
			in.close();
			out.close();
		} catch (Throwable e) {
			e.printStackTrace();
			exit(1);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Diagnostics;
using System.Linq;

namespace AtCoder.C
{
    public class Program
    {
        public static long N { get; private set; }
        public static int Max { get; private set; }
        public static Dictionary<long, long> Sarusa { get; private set; }

        public static void Main() { var r = GetResult(); Debug.WriteLine(r); Console.Write(r); }

        private static object GetResult()
        {
            N = ReadLong();
            var T = ReadText().Replace("SS", string.Empty);
            Max = (int)Math.Pow(3, N);
            var P = Enumerable.Range(0, Max).Select(x => (long)x).ToList();

            CreateSarusaMap();

            for (int i = 0; i < T.Length; i++)
            {
                var dance = T[i];
                switch (dance)
                {
                    case 'S':
                        S(P);
                        break;
                    case 'R':
                        R(P);
                        break;
                }
            }

            return string.Join(" ", P);
        }

        private static void CreateSarusaMap()
        {
            var sansin = new Queue<string>();
            sansin.Enqueue("0");
            sansin.Enqueue("1");
            sansin.Enqueue("2");

            for (var i = 0; i < 11; i++)
            {
                var count = sansin.Count;
                for (var j = 0; j < count; j++)
                {
                    var x = sansin.Dequeue();
                    sansin.Enqueue(x + "0");
                    sansin.Enqueue(x + "1");
                    sansin.Enqueue(x + "2");
                }
            }

            Sarusa = new Dictionary<long, long>() { };
            while (sansin.Count > 0)
            {
                var x = sansin.Dequeue();
                var key = 0L;
                var val = 0L;
                for (var i = 0; i < 12; i++)
                {
                    var s = long.Parse(x[i].ToString());
                    key += (long)Math.Pow(3, i) * s;
                    val += (long)Math.Pow(3, i) * (s == 1 ? 2 : s == 2 ? 1 : 0);
                }

                Sarusa.Add(key, val);
            }
        }

        private static void S(List<long> p)
        {
            for (var i = 0; i < Max; i++)
            {
                p[i] = Sarusa[p[i]];
            }
        }
        private static void R(List<long> p)
        {
            for (var i = 0; i < Max; i++)
            {
                p[i]++;
                if (p[i] == Max)
                {
                    p[i] = 0;
                }
            }
        }
        private static void R(List<long> p, long count)
        {
            for (var i = 0; i < Max; i++)
            {
                p[i] += count;
                if (p[i] >= Max)
                {
                    p[i] -= Max;
                }
            }
        }

        public static string ReadText() { return Console.ReadLine(); }
        public static List<string> ReadTexts() { return Console.ReadLine().Split(' ').ToList(); }
        public static long ReadLong() { return long.Parse(Console.ReadLine()); }
        public static (long, long) Read2Longs() { var ls = ReadLongs(); return (ls[0], ls[1]); }
        public static (long, long, long) Read3Longs() { var ls = ReadLongs(); return (ls[0], ls[1], ls[2]); }
        public static (long, long, long, long) Read4Longs() { var ls = ReadLongs(); return (ls[0], ls[1], ls[2], ls[3]); }
        public static (long, long, long, long, long) Read5Longs() { var ls = ReadLongs(); return (ls[0], ls[1], ls[2], ls[3], ls[4]); }
        public static List<long> ReadLongs() { return Console.ReadLine().Split(' ').Select(x => long.Parse(x)).ToList(); }

    }
}

