namespace Program {
	public class Solver {
		Random rnd = new Random(0);
		public void Solve() {
			var n = ri;
			var s = rs;
			var m = s.Length;
			var end = new int[m + 1];
			bool flip = false;
			Func<int, int> f = x => (flip ? x * 2 : x) % 3;
			var ans = new List<int>() { 0 };
			int N = 1;
			for (int i = 0; i < n; i++) {
				flip = false;
				var bucket = Enumerate(N * 3, x => x / N);
				for (int j = 0; j < m; j++) {
					if (s[j] == 'S') { flip ^= true; }
					else {
						var l = -1;
						for (int k = 0; k < 3; k++) {
							var last = end[j] + k * N;
							var pos = f(bucket[last]);
							if (pos == 2) l = last;
							bucket[last] = f((pos + 1) % 3);
						}
						end[j] = l;
					}
				}
				for (int j = 0; j < 2 * N; j++) ans.Add(ans[j % N]);
				for (int j = 0; j < 3 * N; j++) ans[j] += f(bucket[j]) * N;
				N *= 3;
			}
			Console.WriteLine(ans.AsJoinedString());
		}
		const long INF = 1L << 60;
		static int[] dx = { -1, 0, 1, 0 };
		static int[] dy = { 0, 1, 0, -1 };
		static T[] Enumerate<T>(int n, Func<int, T> f) {
			var a = new T[n]; for (int i = 0; i < a.Length; ++i) a[i] = f(i); return a;
		}
		static T[][] Enumerate<T>(int n, int m, Func<int, int, T> f) {
			return Enumerate(n, x => Enumerate(m, y => f(x, y)));
		}
		static public void Swap<T>(ref T a, ref T b) { var tmp = a; a = b; b = tmp; }
	}
}

using System;
using System.Collections.Generic;
using System.Diagnostics;
using System.Linq;

namespace AtCoder.C
{
    public class Program
    {
        public static long N { get; private set; }
        public static int Max { get; private set; }
        public static Dictionary<long, long> Sarusa { get; private set; }

        public static void Main() { var r = GetResult(); Debug.WriteLine(r); Console.Write(r); }

        private static object GetResult()
        {
            N = ReadLong();
            var T = ReadText().Replace("SS", "");

            Max = (int)Math.Pow(3, N);
            var P = Enumerable.Range(0, Max).Select(x => (long)x).ToList();

            CreateSarusaMap();

            for (int i = 0; i < T.Length; i++)
            {
                var dance = T[i];
                switch (dance)
                {
                    case 'S':
                        S(P);
                        break;
                    case 'R':
                        R(P);
                        break;
                }
            }

            return string.Join(" ", P);
        }

        private static void CreateSarusaMap()
        {
            var sansin = new Queue<string>();
            sansin.Enqueue("0");
            sansin.Enqueue("1");
            sansin.Enqueue("2");

            for (var i = 0; i < 11; i++)
            {
                var count = sansin.Count;
                for (var j = 0; j < count; j++)
                {
                    var x = sansin.Dequeue();
                    sansin.Enqueue(x + "0");
                    sansin.Enqueue(x + "1");
                    sansin.Enqueue(x + "2");
                }
            }

            Sarusa = new Dictionary<long, long>() { };
            while (sansin.Count > 0)
            {
                var x = sansin.Dequeue();
                var key = 0L;
                var val = 0L;
                for (var i = 0; i < 12; i++)
                {
                    var s = long.Parse(x[i].ToString());
                    key += (long)Math.Pow(3, i) * s;
                    val += (long)Math.Pow(3, i) * (s == 1 ? 2 : s == 2 ? 1 : 0);
                }

                Sarusa.Add(key, val);
            }
        }

        private static void S(List<long> p)
        {
            for (var i = 0; i < Max; i++)
            {
                p[i] = Sarusa[p[i]];
            }
        }
        private static void R(List<long> p)
        {
            for (var i = 0; i < Max; i++)
            {
                p[i]++;
                if (p[i] == Max)
                {
                    p[i] = 0;
                }
            }
        }
        private static void R(List<long> p, long count)
        {
            for (var i = 0; i < Max; i++)
            {
                p[i] += count;
                if (p[i] >= Max)
                {
                    p[i] -= Max;
                }
            }
        }

        public static string ReadText() { return Console.ReadLine(); }
        public static List<string> ReadTexts() { return Console.ReadLine().Split(' ').ToList(); }
        public static long ReadLong() { return long.Parse(Console.ReadLine()); }
        public static (long, long) Read2Longs() { var ls = ReadLongs(); return (ls[0], ls[1]); }
        public static (long, long, long) Read3Longs() { var ls = ReadLongs(); return (ls[0], ls[1], ls[2]); }
        public static (long, long, long, long) Read4Longs() { var ls = ReadLongs(); return (ls[0], ls[1], ls[2], ls[3]); }
        public static (long, long, long, long, long) Read5Longs() { var ls = ReadLongs(); return (ls[0], ls[1], ls[2], ls[3], ls[4]); }
        public static List<long> ReadLongs() { return Console.ReadLine().Split(' ').Select(x => long.Parse(x)).ToList(); }

    }
}

using System;
