namespace AtCoder.Abc
{
    class QuestionC
    {
        public static void Main(string[] args)
        {
            var sw = new System.IO.StreamWriter(Console.OpenStandardOutput()) { AutoFlush = false };
            Console.SetOut(sw);

            // 
            long n = long.Parse(Console.ReadLine());

            // 
            string s = Console.ReadLine();


            string[] a = new string[(int)Math.Pow(3,(double)n)];
            for (int i = 0; i < (int)Math.Pow(3, (double)n); i++)
            {
                a[i] = IntToN(i,3);
            }

            var result = new List<string>();
            foreach (var item in a) 
            {
                string str = item;
                foreach (char c in s)
                {
                    if (c == 'S')
                    {
                        string r = "";
                        foreach (var i in str)
                        {
                            if (i == '0')
                            {
                                r += "0";
                            }
                            else if (i == '1')
                            {
                                r += "2";
                            }
                            else
                            {
                                r += "1";
                            }
                        }
                        str = r.ToString();
                    }
                    else
                    {
                        str = IntToN(NToInt(str, 3) + 1 == (int)Math.Pow(3, (double)n) ? 0 : NToInt(str, 3) + 1, 3);
                    }
                }
                result.Add(str);
            }

            Console.WriteLine(result.JoinString(" ", x => NToInt(x, 3).ToString()));

            Console.Out.Flush();
        }
        /// <summary>
        /// int  N 
        /// </summary>
        /// <param name="a"></param>
        /// <param name="sp"></param>
        /// <returns></returns>
        private static string IntToN(int a, int sp)
        {
            return ByteToN(BitConverter.GetBytes(a), sp);
        }

        /// <summary>
        ///  N 
        /// </summary>
        /// <param name="b"></param>
        /// <param name="sp"></param>
        /// <returns></returns>
        private static string ByteToN(byte[] b, int sp)
        {
            long n = BitConverter.ToUInt32(b, 0);
            long num = n;

            char[] ch = getChar(sp);

            string result = string.Empty;

            long amari = 0;
            while (true)
            {
                if (num < sp)
                {
                    result = ch[num] + result;
                    break;
                }
                amari = num % sp;
                num = num / sp;
                result = ch[amari] + result;
            }
            return result;
        }

        /// <summary>
        /// N 
        /// </summary>
        /// <param name="s"></param>
        /// <param name="sp"></param>
        /// <returns></returns>
        private static int NToInt(string s, int sp)
        {
            return BitConverter.ToInt32(NToByte(s, sp), 0);
        }

        /// <summary>
        /// N 
        /// </summary>
        /// <param name="s"></param>
        /// <param name="sp"></param>
        /// <returns></returns>
        private static byte[] NToByte(string s, int sp)
        {
            s = s.ToLower();
            char[] ch = getChar(sp);

            long result = 0;
            int len = s.Length;

            int keta = 0;
            for (int i = len - 1; i >= 0; i--)
            {
                keta = len - i - 1;

                char target = s[i];
                int index = Array.IndexOf(ch, target);
                if (index < 0)
                {
                    throw new IndexOutOfRangeException();
                }
                result += index * ((long)Math.Pow(sp, keta));
            }

            uint are = (uint)(result & 0xFFFFFFFF);
            byte[] b = BitConverter.GetBytes(are);
            return b;
        }

        /// <summary>
        /// 
        /// </summary>
        /// <param name="count"></param>
        /// <returns></returns>
        private static char[] getChar(int count)
        {
            char[] result = new char[count];
            for (int i = 0; i < 10 && i < count; i++)
            {
                result[i] = (char)('0' + ((char)i));
            }
            if (count > 10)
            {
                for (int i = 0; i < count - 10; i++)
                {
                    result[i + 10] = (char)('a' + ((char)i));
                }
            }
            return result;
        }
    }
    public static class IEnumerableUtil
    {
        public static string JoinString<T>(this IEnumerable<T> values, string separator, Func<T, string> converter = null)
        {
            if (converter != null)
            {
                List<string> buff = new List<string>();
                foreach (var item in values)
                    buff.Add(converter(item));
                return string.Join(separator, buff);
            }
            else
                return string.Join(separator, values);
        }
    }
}

