	static int salsa_make(int num)
	{
		int i;
		Queue<int> iQueue = new Queue<int>();
		String str;
		int n = 1;
		int result = 0;

		while (true)
		{
			switch (num % 3)
			{
				case 0: iQueue.Enqueue(0); break;
				case 1:	iQueue.Enqueue(2); break;
				case 2: iQueue.Enqueue(1); break;
			}
			num /= 3;

			if (num == 0)
			{
				break;
			}
		}

		while(iQueue.Count > 0)
		{
			result += iQueue.Dequeue() * n;
			n *= 3;
		}

		return result;
	}

