internal partial class Solver {
    public void Run() {
        var n = ni();
        var t = ns();
        var three = new long[n + 1];
        three[0] = 1;
        for (int i = 1; i < three.Length; i++) {
            three[i] = three[i - 1] * 3;
        }
        var s = new string[n + 1, three[n]];
        s[0, 0] = t;
        var ans = new long[three[n]];
        var builder = new StringBuilder();
        for (int i = 0; i < n; i++) {
            var pattern = three[i + 1];
            for (int j = 0; j < pattern; j++) {
                int bit = (int)(j / three[i]);
                int mod = (int)(j % three[i]);
                var p = s[i, mod];
                builder.Clear();
                for (int k = 0; k < p.Length; k++) {
                    if (p[k] == 'S') {
                        if (k + 1 < p.Length && p[k + 1] == 'S') {
                            // ignore SS
                            k++;
                            continue;
                        }
                        builder.Append('S');
                        if (bit == 1) bit = 2;
                        else if (bit == 2) bit = 1;
                    } else { // R
                        bit++;
                        if (bit == 3) {
                            builder.Append('R');
                            bit = 0;
                        }
                    }
                }
                s[i + 1, j] = builder.ToString();
                for (long k = j; k < ans.Length; k += pattern) {
                    ans[k] += bit * three[i];
                }
            }
        }
        cout.WriteLine(string.Join(" ", ans));
    }
}

