def S(x, n):
    ex = [0]*n
    for j in range(len(x)//3):

        k = j
        tmp = 0
        m = 3
        q = j*3
        while k > 0:
            if k%3 == 1:
                tmp = tmp + 2*m
            elif k%3 == 2:
                tmp = tmp + 1*m
            k = k//3
            m = m*3
        ex[tmp] = x[q]
        ex[tmp + 2] = x[q +1]
        ex[tmp + 1] = x[q +2]
    return(ex)


