import sys

def ternary(n):
    if n == 0:
        return '0'
    nums = []
    while n:
        n, r = divmod(n,3)
        nums.append(str(r))
    return ''.join(reversed(nums))

def swap(str):
    swapped = ''
    for c in str:
        if c == '1': swapped += '2'
        elif c == '2': swapped += '1'
        else: swapped += c
    return swapped

