import string
def ternary (n):
    if n == 0:
        return '0'
    nums = []
    while n:
        n, r = divmod(n, 3)
        nums.append(str(r))
    return ''.join(nums)
def to_dec(strs):
    num = 0
    for i in range(len(strs)):
        num += 3**(i)*int(strs[i])
    return num

def solve(N, T):
    dic = {}
    total = 3**N
    for i in range(3**N):
        if i in dic:
            continue
        te = ternary(i)
        new_te = te.replace('1', '%temp%').replace('2', '1').replace('%temp%', '2') 
        new_i = to_dec(new_te)
        if new_i != i:
            dic[i] = new_i
            dic[new_i] = i
    person_2_seat = {i:i for i in range(total)}
    seat_2_person = {i:i for i in range(total)}
    ans = []
    for c in T:
        if c =='S':
            seen = set()
            for k in dic:
                if k in seen: continue
                first, second = k, dic[k]
                p1, p2 = seat_2_person[first], seat_2_person[second]
                person_2_seat[p1], person_2_seat[p2] = second, first
                seat_2_person[first], seat_2_person[second] = p2, p1
                seen.add(second)
        else:
            new_person_2_seat, new_seat_2_person = {}, {}
            for k, v in seat_2_person.items():
                new_seat = (k+1) % total
                new_person_2_seat[v] = new_seat
                new_seat_2_person[new_seat] = v
            person_2_seat, seat_2_person =new_person_2_seat, new_seat_2_person
    ans = []
    for i in range(total):
        ans.append(str(person_2_seat[i]))
    return ' '.join(ans)


