    Num[i] = Num[i-1] + list(map(lambda x:x+2*3**(i),Num[i-1])) + list(map(lambda x:x+3**(i),Num[i-1]))
def Q(Num):
    ans = 0
    while Num > 1:
        Num = Num // 3
        ans += 1
    return ans
def R(List):
  Num = len(List)
  if Num == 1:
    return [List[0],List[2],List[1]]
  else:
    A = Q(Num) - 1
    L = List[Num[A][i] for i in range(Num)]
    return L
def S(List):
