def transform(place):
    result = []
    while True:
        place, remain = divmod(place, 3)
        result.append(remain)
        if place == 0:
            break

    for i in range(0, len(result)):
        if result[i] == 1:
            result[i] = 2
        elif result[i] == 2:
            result[i] = 1

    sum = 0
    for i in range(0, len(result)):
        sum += result[i] * (3 ** i)

    return sum


