def cvt(a):
    b = 0
    c = 0
    while a != 0:
        d = a % 3
        a = (a-(a % 3)) / 3
        if d == 1:
            d = 2
        elif d == 2:
            d = 1
        b += d * (3**c)
        c += 1
    return int(b)
