import math
def Salsa(N_Pow, Len):
    if(N_Pow==1):
        Dest = [0] * 3
        Dest[0] = 0
        Dest[1] = 2
        Dest[2] = 1
        return Dest
    else:
        Dest = [0] * (Len//3)
    Dest[0] = 0
    Dest[1] = 2
    Dest[2] = 1

    Value_Min = 1
    for Pow in range(1, N_Pow-1):
        Value_Min *= 3
        Value_Max = Value_Min *2-1
        for Num in range(Value_Min, Value_Max+1, 1):
            Value2 = Dest[Num-Value_Min] + 2 * Value_Min
            Dest[Num] = Value2
            Dest[Value2] = Num
    return Dest



def moveSalsa(Dest_Salsa, PosList):
    N = len(PosList)
    N_3 = N//3
    N_2_3 = N_3 *2
    for Num in range(N):
        if(PosList[Num] >= N_2_3):
            PosList[Num] = Dest_Salsa[ PosList[Num]-N_2_3 ] + N_3
        elif(PosList[Num] >= N_3):
            PosList[Num] = Dest_Salsa[ PosList[Num]-N_3 ] + N_2_3
        else:
            PosList[Num] = Dest_Salsa[ PosList[Num] ]


def moves(N_Pow, PosList, Str):
    MoveTimes = len(Str)
    Len = len(PosList)

    Dest_Salsa = Salsa(N_Pow, Len)
    k =0
    while(k < MoveTimes):
        SameTimes = 0
        if(Str[k] == 'S'):
            k += 1
            SameTimes += 1
            while(k < MoveTimes and Str[k] == 'S'):
                k += 1
                SameTimes += 1
            if(SameTimes%2 !=0):
                moveSalsa(Dest_Salsa, PosList)
        else:
            k += 1
            SameTimes += 1
            while(k < MoveTimes and Str[k] == 'R'):
                k += 1
                SameTimes += 1

            for Num in range(Len):
                PosList[Num] = (PosList[Num]+SameTimes) % Len

    Str = ""
    for Num in range( Len ):
        Str = Str + str(PosList[Num]) + " "
    print(Str)

