def Salsa(N_Pow, Len):
    Dest = [0] * Len
    Dest[0] = 0
    Dest[1] = 2
    Dest[2] = 1

    Value_Min = 1
    for Pow in range(1, N_Pow):
        Value_Min *= 3
        Value_Max = Value_Min *2-1
        for Num in range(Value_Min, Value_Max+1, 1):
            Value2 = Dest[Num-Value_Min] + 2 * Value_Min
            Dest[Num] = Value2
            Dest[Value2] = Num
    return Dest



