memo = [0] * 600000


def SRS(num, memo):
    if num in memo:
        return memo[num]
    a = 0
    num_root = num
    for n in range(11, -1, -1):
        if int(num / 3**n) == 1:
            a += 3**n
        elif int(num / 3**n) == 2:
            a -= 3**n
        num %= 3**n
    num = num_root + a
    memo[num_root] = num
    memo[num] = num_root
    return num


