import sys


def salsa(Q):
    l = len(Q) // 3
    Q0 = Q[0: l]
    Q1 = Q[l: 2 * l]
    Q2 = Q[2 * l:3 * l]
    if l != 1:
        Q0 = salsa(Q0)
        Q1 = salsa(Q1)
        Q2 = salsa(Q2)
    Q = Q0 + Q2 + Q1
    return Q


