    X_dumy = X
    out = ''
    while X_dumy>0:
        out = str(X_dumy%n)+out
        X_dumy = int(X_dumy/n)
    if out == '':
        out = '0'
    return out

def sarusa(n):
    n = Base_10_to_n(n, 3)
    n = n.replace('1', '3')
    n = n.replace('2', '1')
    n = n.replace('3', '2')
    n = int(n, 3)
    return n

def main():
    N = int(readline())
    T = readline().strip().decode()
    ans = [i for i in range(3**N)]
    l = [sarusa(i) for i in range(3**12)]
    for t in T:
        for i in range(3**N):
            if t == 'S':
                if ans[i] != 0:
                    ans[i] = l[ans[i]]
            elif ans[i] == 3**N-1:
                ans[i] = 0
            else:
                ans[i] += 1

    print(' '.join(list(map(str, ans))))
    return

if __name__ == '__main__':
    main()

