def mod3(n):
  if int(n%3)==1:
    return str(n+1%3)
  elif int(n%3)==2:
    return str(n-1%3)
  else:
    return str(n%3)

def base10to3(n):
  if (int(n/3)):
    return base10to3(int(n/3)) + mod3(n%3)
  return mod3(n%3)

def S(r,n):
  for i in range(len(r)):
    m=list(base10to3(r[i]))
    r[i]=int(''.join(m),3)
  return r

def R(r,n):
  for i in range(len(r)):
    if r[i] ==3**n-1:
      r[i]=0
    else:
      r[i] +=1
  return r

