use proconio::*;
use proconio::marker::Chars;

fn salsa(x: usize) -> usize {
    assert!(x < 3);
    if x == 0 {
        0
    } else {
        x ^ 3
    }
}

fn test() {
    input! {
        n: usize,
        s: Chars,
    }
    let mut sum = vec![0; s.len() + 1];
    let mut rumba = vec![];
    for (i, (s, c)) in sum.iter_mut().zip(s.iter()).enumerate() {
        if *c == 'S' {
            *s = 1;
        } else {
            rumba.push(i);
        }
    }
    for i in (0..s.len()).rev() {
        sum[i] += sum[i + 1];
    }
    let mut dp = vec![(rumba, 0)];
    for i in 0..n {
        let len = 3usize.pow(i as u32);
        let mut next: Vec<_> = vec![(vec![], 0); 3 * len];
        for (i, (rumba, pos)) in dp.into_iter().enumerate() {
            let mut prev = 0;
            let mut a = vec![0, 1, 2];
            for x in rumba {
                if (sum[prev] - sum[x]) % 2 == 1 {
                    for y in a.iter_mut() {
                        *y = salsa(*y);
                    }
                }
                for (j, y) in a.iter_mut().enumerate() {
                    if *y == 2 {
                        *y = 0;
                        next[j * len + i].0.push(x);
                    } else {
                        *y += 1;
                    }
                }
                prev = x;
            }
            if sum[prev] % 2 == 1 {
                for y in a.iter_mut() {
                    *y = salsa(*y);
                }
            }
            for (j, &y) in a.iter().enumerate() {
                next[j * len + i].1 = y * len + pos;
            }
        }
        dp = next;
    }
    let mut s = String::new();
    for dp in dp {
        s.push_str(&format!("{} ", dp.1));
    }
    s.pop();
    println!("{}", s);
}

fn main() {
    test();
}
