        if (parent[x] !== x) {
            parent[x] = find(parent, parent[x]);
        }
        return parent[x];
    }

    function union(parent, rank, x, y) {
        let rootX = find(parent, x);
        let rootY = find(parent, y);

        if (rootX !== rootY) {
            if (rank[rootX] > rank[rootY]) {
                parent[rootY] = rootX;
            } else if (rank[rootX] < rank[rootY]) {
                parent[rootX] = rootY;
            } else {
                parent[rootY] = rootX;
                rank[rootX]++;
            }
            return true;
        }
        return false;
    }

    const parent = Array.from({ length: N }, (_, i) => i);
    const rank = new Array(N).fill(0);

    // Process clues where C_i = 0 (exactly one simple path)
    for (const [A_i, B_i, C_i] of clues) {
        if (C_i === 0) {
            if (find(parent, A_i) === find(parent, B_i)) {
                return false;
            }
            union(parent, rank, A_i, B_i);
        }
    }

    // Check clues where C_i = 1 (two or more simple paths)
    for (const [A_i, B_i, C_i] of clues) {
        if (C_i === 1 && find(parent, A_i) === find(parent, B_i)) {
            return true;
        }
    }

    return false;
}

