from collections import defaultdict, deque

def solve(N, M, Q, clues):
    def count_paths(graph, start, end):
        visited = [False] * N
        path_count = 0

        def dfs(node, target, curr_path):
            nonlocal path_count

            if node == target:
                path_count += 1
                return

            visited[node] = True

            for neighbor in graph[node]:
                if not visited[neighbor]:
                    dfs(neighbor, target, curr_path + [neighbor])

            visited[node] = False

        dfs(start, end, [start])
        return path_count

    def is_valid_graph():
        for a, b, c in clues:
            graph = defaultdict(list)
            edges_added = set()

            # Try to construct a graph with the given constraint
            if c == 0:  # exactly one path
                # Attempt to create a graph with exactly one path
                for u, v in combinations(range(N), 2):
                    if len(graph[u]) < 2 and len(graph[v]) < 2:
                        if (u, v) not in edges_added and (v, u) not in edges_added:
                            graph[u].append(v)
                            graph[v].append(u)
                            edges_added.add((u, v))

                    if len(graph) >= M:
                        break

                # Check path constraint
                paths = count_paths(graph, a, b)
                if paths != 1:
                    return False

            else:  # two or more paths
                # Attempt to create a graph with multiple paths
                for u, v in combinations(range(N), 2):
                    if (u, v) not in edges_added and (v, u) not in edges_added:
                        graph[u].append(v)
                        graph[v].append(u)
                        edges_added.add((u, v))

                    if len(graph) >= M:
                        break

                # Check path constraint
                paths = count_paths(graph, a, b)
                if paths < 2:
                    return False

        return True

    from itertools import combinations
    return "Yes" if is_valid_graph() else "No"

