                this.parent[x] = this.find(this.parent[x]);
            }
            return this.parent[x];
        }

        union(x, y) {
            let rootX = this.find(x);
            let rootY = this.find(y);

            if (rootX === rootY) return false;

            if (this.rank[rootX] < this.rank[rootY]) {
                [rootX, rootY] = [rootY, rootX];
            }

            this.parent[rootY] = rootX;

            if (this.rank[rootX] === this.rank[rootY]) {
                this.rank[rootX]++;
            }

            return true;
        }
    }

    // Try to satisfy the clues
    for (let mask = 0; mask < (1 << Q); mask++) {
        let uf = new UnionFind(N);
        let edgeCount = 0;
        let valid = true;

        // Process clues based on current mask
        for (let i = 0; i < Q; i++) {
            let [a, b, c] = clues[i];

            // If 1-bit path clue, force multiple paths by skipping union
            if (c === 1 && ((mask & (1 << i)) === 0)) {
                if (uf.find(a) === uf.find(b)) {
                    valid = false;
                    break;
                }
            }
            // If 0-bit path clue, ensure single path
            else if (c === 0) {
                if (!uf.union(a, b)) {
                    valid = false;
                    break;
                }
                edgeCount++;
            }
        }

        // Check total edge count and connectivity
        if (valid && edgeCount <= M) {
            let roots = new Set();
            for (let i = 0; i < N; i++) {
                roots.add(uf.find(i));
            }

            if (roots.size === 1) {
                return true;
            }
        }
    }

    return false;
}

