using namespace std;

const int MAX_N = 1000;
int parent[MAX_N];
int dist[MAX_N];
bool hasCycle[MAX_N];
bool visited[MAX_N];

int find(int x) {
    if (parent[x] != x)
        parent[x] = find(parent[x]);
    return parent[x];
}

void unionSets(int x, int y) {
    int rootX = find(x);
    int rootY = find(y);
    if (rootX != rootY) {
        parent[rootX] = rootY;
    }
}

bool bfs(int start, const vector<set<int>>& adj) {
    memset(dist, -1, sizeof(dist));
    memset(hasCycle, false, sizeof(hasCycle));
    queue<int> q;
    q.push(start);
    dist[start] = 0;

    while (!q.empty()) {
        int u = q.front();
        q.pop();

        for (int v : adj[u]) {
            if (dist[v] == -1) {
                dist[v] = dist[u] + 1;
                q.push(v);
            } else if (dist[v] >= dist[u]) {
                hasCycle[v] = true;
            }
        }
    }

    return any_of(hasCycle, hasCycle + adj.size(), [](bool b) { return b; });
}

bool graphExists(int N, vector<tuple<int, int, int>>& clues) {
    vector<set<int>> adj(N);

    for (int i = 0; i < N; ++i) {
        parent[i] = i;
    }

    for (auto& clue : clues) {
        int A, B, C;
        tie(A, B, C) = clue;

        if (C == 0) {
            if (find(A) == find(B)) {
                return false;
            }
            adj[A].insert(B);
            adj[B].insert(A);
            unionSets(A, B);
        } else {
            if (find(A) == find(B)) {
                continue;
            }
            adj[A].insert(B);
            adj[B].insert(A);
        }
    }

    for (int i = 0; i < N; ++i) {
        if (!visited[i]) {
            if (bfs(i, adj)) {
                return true;
            }
        }
    }

    return false;
}

