typedef long long ll;

const int N = 505;
const ll INF = 1e18;

int n, a[N][N];
ll ans = INF, s1[N][N], s2[N][N], f[N][N];

int main()
{
	read(n);
	for (int i = 1; i <= n; i++) for (int j = 1; j <= n; j++)
		if (i != j) read(a[i][j]);
	for (int i = 1; i <= n; i++) for (int j = 1; j < i; j++)
		s1[i][j] = s1[i][j - 1] + a[j][i], s2[i][j] = s2[i][j - 1] + a[i][j];
	for (int i = 0; i < N; i++) for (int j = 0; j < N; j++)
		f[i][j] = INF; f[1][1] = 0;
	for (int i = 1; i <= n; i++) for (int j = 1; j <= i; j++)
	{
		ll cost = 0;
		for (int k = i + 1; k <= n; k++)
			f[k][i] = Min(f[k][i], f[i][j] + cost + s2[k][i - 1]),
				cost += s2[k][j - 1] + s1[k][k - 1] - s1[k][i - 1];
		ans = Min(ans, f[i][j] + cost);
	}
	return std::cout << ans << std::endl, 0;
}

