using namespace std;

using ll = long long;
const int N = 505;

int a[N][N];
ll fromL[N][N];
ll fromR[N][N];
ll f[N][N];

int main() {
	ios_base::sync_with_stdio(false);
	int n;
	cin >> n;
	for (int i = 0; i < n; ++i) {
		for (int j = 0; j < n; ++j) if (i != j) {
			cin >> a[i][j];
		}
	}
	for (int i = 0; i < n; ++i) {
		for (int j = 0; j < n; ++j) if (i != j) {
			if (i < j) {
				fromL[i + 1][j + 1] = a[i][j];
			} else {
				fromR[i + 1][j + 1] = a[i][j];
			}
		}
	}
	for (int i = 0; i < n; ++i) {
		for (int j = 0; j < n; ++j) {
			fromL[i + 1][j + 1] += fromL[i + 1][j] + fromL[i][j + 1] - fromL[i][j];
			fromR[i + 1][j + 1] += fromR[i + 1][j] + fromR[i][j + 1] - fromR[i][j];
		}
	}
	for (int i = 1; i < n; ++i) {
		f[0][i] = fromR[i][i];
	}
	for (int i = 1; i < n; ++i) {
		for (int j = i + 1; j <= n; ++j) {
			for (int k = 0; k < i; ++k) {
				ll cost = f[k][i];
				cost += fromR[j][j] - fromR[i][j] - fromR[j][k] + fromR[i][k];
				cost += fromL[i][j] - fromL[i][i];
				f[i][j] = max(f[i][j], cost); 
			}
		}
	}
	ll ans = 0;
	for (int i = 0; i < n; ++i) {
		ans = min(ans, -f[i][n]);
	}
	for (int i = 0; i < n; ++i) {
		for (int j = 0; j < n; ++j) {
			ans += a[i][j];
		}
	}
	cout << ans << "\n";
}

