#include <bits/stdc++.h>

using namespace std;

#define	INF	1e16

int main(int argc, char *argv[])
{
	ios::sync_with_stdio(false); cin.tie(0);

	int n;
	cin >> n;
	vector<vector<long long> > a(n, vector<long long>(n));
	for (int i = 0; i < n; i++)
		for (int j = 0; j < n; j++)
			if (i != j) cin >> a[i][j];

	vector<vector<long long> > dp(n + 1, vector<long long>(n + 1, INF));
	dp[0][0] = 0;

	for (int i = 1; i <= n; i++) {
		vector<long long> x(i), y(i);
		for (int j = 0; j < i; j++) {
			for (int k = i + 1; k <= n; k++) x[j] += a[k - 1][j - 1];
			if (j > 0) x[j] += x[j - 1];
		}
		for (int j = i - 1; j >= 0; j--) {
			for (int k = j + 1; k <= i; k++) y[j] += a[j][k - 1];
			if (j + 1 < i - 1) y[j] += y[j + 1];
		}
		for (int j = 0; j < i; j++)
			for (int k = 0; k <= j; k++)
				dp[i][j] = min(dp[i][j], dp[j][k] + x[j] - x[k] + y[j]);
	}

	cout << *min_element(dp[n].begin(),dp[n].end()) << endl;

	return 0;
}

