typedef long long LL;

const int N=505;
LL n,a[N][N],b[N][N],dp[N][N];
inline LL geta(int s1,int s2,int t1,int t2) {
    if(s1>s2 || t1>t2) return 0;
    return a[s2][t2]-(s1?a[s1-1][t2]:0)-(t1?a[s2][t1-1]:0)+((s1&&t1)?a[s1-1][t1-1]:0);
}
inline LL getb(int s1,int s2,int t1,int t2) {
    if(s1>s2 || t1>t2) return 0;
    return b[s2][t2]-(s1?b[s1-1][t2]:0)-(t1?b[s2][t1-1]:0)+((s1&&t1)?b[s1-1][t1-1]:0);
}
inline LL trans(int x,int y,int z) {
    LL sum=getb(1,x,y+2,z+1);
    sum+=getb(x+1,y,z+1,z+1);
    sum+=geta(y+1,z,y+2,z);
    return sum;
}

int main() {
