	}


	const ll inf = 1e18;
	VV<ll> dp(N,V<ll>(N,inf)),ndp(N,V<ll>(N,inf));
	dp[0][0] = 0;
	rep1(i,N-1){
		V<ll> Rs(N+1),Ls(N+1);
		rep1(x,N) Rs[x] = Rs[x-1] + A[x-1][i];
		rep1(x,N) Ls[x] = Ls[x-1] + A[i][x-1];
		auto R = [&](int l,int r,int i){	//[l,r) -> i
			return Rs[r] - Rs[l];
		};
		auto L = [&](int l,int r,int i){	//[l,r) <- i
			return Ls[r] - Ls[l];
		};
		rep(j,N) rep(k,N) ndp[j][k] = inf;
		rep(j,i) rep(k,max(j,1)){
			chmin(ndp[j][k],dp[j][k] + R(j,i,i) + L(0,k,i));
			chmin(ndp[i][j],dp[j][k] + L(0,j,i));
		}
		swap(dp,ndp);
	}
	ll ans = inf;
	rep(j,N) rep(k,N) chmin(ans,dp[j][k]);
