void solve() {
	int n; cin >> n;
	vector<vector<ll>> a(n);
	rep(i, n) {
		a[i].resize(n);
		rep(j, n) {
			if (i == j)a[i][j] = 0;
			else cin >> a[i][j];
		}
	}

	//calc sum
	rep(i, n) {
		ll s = 0;
		for (int j = i + 1; j < n; j++) {
			for (int k = i; k < j; k++) {
				s += a[k][j];
			}
			sum[i][j] = s;
		}
	}

	rep(i, n + 1)rep(j, n + 1)dp[i][j] = INF;
	dp[0][0] = 0;
	{
		ll s = 0;
		for (int j = n-1; j>0; j--) {
			for (int k = 0; k <= j; k++) {
				dp[j][k] = s + sum[0][k] + sum[k + 1][j];
			}

			s += a[j][0];
		}
		/*cout << "0 is result " << endl;
		rep(j, n)rep(k, n) {
			cout << j << " " << k << " " << dp[j][k] << endl;
		}*/
	}
	for (int i = 1; i < n-1; i++) {
		rep(j, n + 1)rep(k, n + 1)cop[j][k] = INF;
		for (int j = n - 1; j >= i; j--) {
			for (int k = 0; k <= j; k++) {
				if (dp[j][k] == 0)continue;
				ll s = 0;
				if (k >= i) {
					for (int l = n - 1; l >= j; l--) {
						cop[l][k] = min(cop[l][k], s+dp[j][k] + sum[k + 1][l] - sum[k + 1][j]);
						s += a[l][i];
					}
				}
				else {
					for (int l = n - 1; l >= j; l--) {
						for (int m = j; m <= l; m++) {
							cop[l][m] = min(cop[l][m], s + dp[j][k] + sum[i][m] - sum[i][j] + sum[m+1][l]);
						}

						s += a[l][i];
					}
				}
			}
		}
		rep(j, n + 1)rep(k, n + 1)dp[j][k]=cop[j][k];

		/*cout << i << " is result " << endl;
		rep(j, n)rep(k, n) {
			cout << j << " " << k << " " << dp[j][k] << endl;
		}*/
	}
	ll ans = INF;
	/*rep(i, n)rep(j, n) {
		cout << i << " " << j << " " << dp[i][j] << endl;
	}*/
	rep(i, n+1)rep(j, n+1)ans = min(ans, dp[i][j]);
	cout << ans << endl;
}

