class edge {
public:
    int to;
    llint cap;
    llint cost;
    llint rev;
    bool isrev;
};
edge make_edge(int a,int b,int c,int d,bool e){
	edge x;
	x.to=a;
	x.cap=b;
	x.cost=c;
	x.rev=d;
	x.isrev=e;
	return x;
}
template< typename flow_t, typename cost_t >
struct PrimalDual {
  const cost_t INF;


  vector< vector< edge > > graph;
  vector< cost_t > potential, min_cost;
  vector< int > prevv, preve;

  PrimalDual(int V) : graph(V), INF(numeric_limits< cost_t >::max()) {}

  void add_edge(int from, int to, flow_t cap, cost_t cost) {
    graph[from].emplace_back(make_edge(to, cap, cost, (int) graph[to].size(), false));
    graph[to].emplace_back(make_edge(from, 0, -cost, (int) graph[from].size() - 1, true));
  }

  cost_t min_cost_flow(int s, int t, flow_t f) {
    int V = (int) graph.size();
    cost_t ret = 0;
    using Pi = pair< cost_t, int >;
    priority_queue< Pi, vector< Pi >, greater< Pi > > que;
    potential.assign(V, 0);
    preve.assign(V, -1);
    prevv.assign(V, -1);

    while(f > 0) {
      min_cost.assign(V, INF);
      que.emplace(0, s);
      min_cost[s] = 0;
      while(!que.empty()) {
        Pi p = que.top();
        que.pop();
        if(min_cost[p.second] < p.first) continue;
        for(int i = 0; i < graph[p.second].size(); i++) {
          edge &e = graph[p.second][i];
          cost_t nextCost = min_cost[p.second] + e.cost + potential[p.second] - potential[e.to];
          if(e.cap > 0 && min_cost[e.to] > nextCost) {
            min_cost[e.to] = nextCost;
            prevv[e.to] = p.second, preve[e.to] = i;
            que.emplace(min_cost[e.to], e.to);
          }
        }
      }
      if(min_cost[t] == INF) return -1;
      for(int v = 0; v < V; v++) potential[v] += min_cost[v];
      flow_t addflow = f;
      for(int v = t; v != s; v = prevv[v]) {
        addflow = min(addflow, graph[prevv[v]][preve[v]].cap);
      }
      f -= addflow;
      ret += addflow * potential[t];
      for(int v = t; v != s; v = prevv[v]) {
        edge &e = graph[prevv[v]][preve[v]];
        e.cap -= addflow;
        graph[v][e.rev].cap += addflow;
      }
    }
    return ret;
  }

  void output() {
    for(int i = 0; i < graph.size(); i++) {
      for(auto &e : graph[i]) {
        if(e.isrev) continue;
        auto &rev_e = graph[e.to][e.rev];
        cerr << i << "->" << e.to << " (flow: " << rev_e.cap << "/" << rev_e.cap + e.cap << ")" << endl;
      }
    }
  }
};


int main(void){
	int n,i,j;cin>>n;
	PrimalDual<llint,llint>gra(n+2);
	llint ans=0;
	for(i=0;i<n-1;i++){gra.add_edge(i,i+1,big,0);}
	vector<llint>ovcap(n);
	for(i=0;i<n;i++){
		for(j=0;j<i;j++){
			llint A;cin>>A;gra.add_edge(i,j,A,1);
		}
		for(j=i+1;j<n;j++){
			llint A;cin>>A;
			ovcap[i]-=A;
			ovcap[j]+=A;
			ans+=A;
			gra.add_edge(j,i,A,1);

		}
	}
	llint allover=0;
	for(i=0;i<n;i++){
		if(ovcap[i]>0){allover+=ovcap[i];gra.add_edge(n,i,ovcap[i],0);}
		else{gra.add_edge(i,n+1,-ovcap[i],0);}
	}
	cerr<<"allover="<<allover<<endl;
	llint flow=gra.min_cost_flow(n,n+1,allover);
	cerr<<"flow="<<flow<<endl;
	cout<<ans-flow<<endl;
	return 0;
}

