const int maxn = 500;
const li infl = ~0LLU >> 2;

int n, a[maxn][maxn];
li dp[maxn][maxn], basic, total;
li sl[maxn + 1][maxn + 1], sr[maxn + 1][maxn + 1];

void Init(void) {
  for (int i = 0; i <= n; ++i) {
    sl[i][i] = sr[i][i] = 0;
  }
  for (int l = n; l >= 0; --l) {
    for (int r = l + 1; r <= n; ++r) {
      sl[l][r] = sl[l][r - 1];
      for (int k = l; k < r - 1; ++k) {
        sl[l][r] += a[r - 1][k];
      }
      sr[l][r] = sr[l + 1][r];
      for (int k = l + 1; k < r; ++k) {
        sr[l][r] += a[l][k];
      }
    }
  }
}

li CalcL(int l, int r) {
  return sl[l][r];
}

li CalcR(int l, int r) {
  return sr[l][r];
}

int main(void) {
  scanf("%d", &n);
  for (int i = 0; i < n; ++i) {
    for (int j = 0; j < n; ++j) {
      if (i != j) {
        scanf("%d", a[i] + j);
      }
    }
  }
  for (int i = 0; i < n; ++i) {
    for (int j = i + 1; j < n; ++j) {
      basic += a[i][j];
      total += a[i][j] + a[j][i];
    }
  }

  Init();

  li global = -infl;
  for (int i = 1; i < n; ++i) {
    for (int j = i + 1; j < n; ++j) {
      // calc (i, j)
      li &ans = dp[i][j];
      ans = CalcL(0, j) - CalcR(0, i) - CalcR(i, j);
      for (int k = 1; k < i; ++k) {
        // try (k, i, j)
        li now = dp[k][i];
        now += CalcL(k, j) - CalcL(k, i) - CalcR(i, j);
        Max(ans, now);
      }
      li now = ans;
      now += CalcL(i, n) - CalcL(i, j) - CalcR(j, n);
      Max(global, now);
    }
  }

  Max(global, CalcL(0, n) - CalcR(0, n));
  for (int i = 1; i < n; ++i) {
    Max(global, CalcL(0, n) - CalcR(0, i) - CalcR(i, n));
  }

  global += basic;
  global = total - global;
  printf("%lld\n", global);
  return 0;
}

