	f[0][0] = 0;
	for (int i = 0; i < n; ++i) {
		for (int j = 0; j <= i; ++j) {
			for (int k = i + 1; k <= n; ++k) {
				f[k][i] = std::min(f[k][i], f[i][j] + getsum(s1, i, i, k, k) + getsum(s2, i, 0, k, j));
			}
		}
	}
	long long ans = INF;
	for (int i = 0; i < n; ++i) {
		ans = std::min(ans, f[n][i]);
	}
	print(ans);
}

