    F0R (i, N) F0R (j, N) pref[i][j+1] = pref[i][j] + a[i][j];

    vvll ferp(N+1, vll(N));
    F0R (i, N) F0R (j, N) ferp[i+1][j] = ferp[i][j] + a[i][j];

    vvll dp(N + 1, vll(N + 1, LLONG_MAX)); dp[0][1] = 0;
    vvll DP = dp;

    FOR (i, 1, N) {
        trav (r, DP) fill(all(r), LLONG_MAX);

        F0R (pc, N + 1) F0R (cc, N + 1) {
            ll cost = dp[pc][cc]; if (cost == LLONG_MAX) continue;
            {
                ll stay = cost;
                // FOR (v, i - cc, i) stay += a[v][i];
                stay += ferp[i][i] - ferp[i-cc][i];
                // F0R (v, i - cc - pc) stay += a[i][v];
                stay += pref[i][i - cc - pc];

                ckmin(DP[pc][cc+1], stay);
            }
            {
                ll go = cost + pref[i][i - cc];
                //F0R (v, i - cc) go += a[i][v];

                ckmin(DP[cc][1], go);
            }
        }

        swap(dp, DP);
    }

    ll ans = LLONG_MAX;
    trav (r, dp) ckmin(ans, *min_element(all(r)));
    ps(ans);

    return 0;
}

