		pos[i][i] = 0;
		for (int j = i - 1; j >= 1; j --) pos[i][j] = pos[i][j+1] + a[j][i];
		neg[i][i] = 0;
		for (int j = i - 1; j >= 1; j --) neg[i][j] = neg[i][j+1] + a[i][j];
	}
