const int maxn = 510;
const LL INF = 0x3f3f3f3f3f3f3f3f;
LL dp[2][maxn][maxn], A[maxn][maxn], B[maxn][maxn];

inline void ckmin(LL &x, LL y) { x >= y ? x = y : 0; }

int main()
{

	int n = read();
	for(int i = 1; i <= n; ++i)
	{
		for(int j = 1; j <= n; ++j) if(i != j)
		{
			int x = read();
			if(i < j) A[j][i] = x;
			else B[i][j] = x;
		}
	}

	for(int i = 1; i <= n; ++i)
		for(int j = 1; j <= i; ++j)
			A[i][j] += A[i][j - 1], B[i][j] += B[i][j - 1];

	int p = 0, q = 1;
	memset(dp, 0x3f, sizeof(dp));
	dp[p][0][0] = 0;
	for(int i = 2; i <= n; ++i)
	{
		swap(p, q);
		for(int j = 0; j <= i; ++j) for(int k = 0; k <= i; ++k) dp[p][j][k] = INF;

		for(int j = 0; j < i; ++j)
			for(int k = j; k < i; ++k) if(dp[q][j][k] != INF)
			{
				LL tmp = dp[q][j][k];
				//if(tmp == 1) cout << '!' << endl;

				if(j == 0 && k == 0)
				{
					ckmin(dp[p][0][0], tmp + A[i][i]);
					ckmin(dp[p][0][i], tmp);
					continue;
				}

				if(j == 0)
				{
					ckmin(dp[p][0][k], tmp + A[i][i] - A[i][k - 1]);
					ckmin(dp[p][k][i], tmp + B[i][k - 1]);
					continue;
				}

				ckmin(dp[p][j][k], tmp + A[i][i] - A[i][k - 1] + B[i][j - 1]);
				ckmin(dp[p][k][i], tmp + B[i][k - 1]);
			}

		/*
		for(int i = 0; i <= n; ++i)
			for(int j = 0; j <= n; ++j) 
			{
				cout << i << ' ' << j << ' ' << dp[p][i][j] << endl;
			}*/
	}

	LL ans = INF;
	for(int i = 0; i <= n; ++i)
		for(int j = 0; j <= n; ++j) 
		{
			//cout << i << ' ' << j << ' ' << dp[p][i][j] << endl;
			ckmin(ans, dp[p][i][j]);
		}
	cout << ans << endl;

	return 0;
}

