}

ll getVal2(int a,int b,int c,int d){
	return sum2[b][d] - (c == 0 ? 0 : sum2[b][c - 1]) - (a == 0 ? 0 : sum2[a - 1][d]) + (a == 0 || c == 0 ? 0 : sum2[a - 1][c - 1]);
