	for (int a = 1;a <= N - 1;++a)
		for (int b = a + 1;b <= N - 1;++b)
		{
			upd(F[a][b], query1(1, a, 1, a) + query1(a + 1, b, a + 1, b) + query2(b + 1, N, 1, a));
			for (int c = b + 1;c <= N - 1;++c)
				upd(F[b][c], F[a][b] + query1(b + 1, c, b + 1, c) + query2(c + 1, N, a + 1, b));
			upd(Ans, F[a][b] + query1(b + 1, N, b + 1, N));
		}
	print(Ans);
	return 0;
}

