int main()
{
    auto N    = in<int>();
    auto fass = make_v({N, N}, 0LL);
    auto bass = make_v({N, N}, 0LL);
    ll btotal = 0;
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            if (i == j) { continue; }
            if (i < j) {
                fass[i][j] = in<ll>();
            } else {
                bass[i][j] = -in<ll>();
                btotal -= bass[i][j];
            }
        }
    }
    auto fsss = fass, bsss = bass;
    for (int i = 0; i < N; i++) {
        for (int j = 1; j < N; j++) { fsss[i][j] += fsss[i][j - 1], bsss[i][j] += bsss[i][j - 1]; }
    }
    for (int i = 1; i < N; i++) {
        for (int j = 0; j < N; j++) { fsss[i][j] += fsss[i - 1][j], bsss[i][j] += bsss[i - 1][j]; }
    }
    auto sum = [&](const auto sss, const int il, const int iu, const int jl, const int ju) {
        ll ans = sss[iu - 1][ju - 1];
        if (il > 0) { ans -= sss[il - 1][ju - 1]; }
        if (jl > 0) { ans -= sss[iu - 1][jl - 1]; }
        if (il > 0 and jl > 0) { ans += sss[il - 1][jl - 1]; }
        return ans;
    };
    auto fsum = [&](const int, const int p, const int np) { return sum(fsss, p, np, p, np); };
    auto bsum = [&](const int pp, const int p, const int np) { return sum(bsss, p, np, pp, np); };
    auto dp   = make_v({N + 1, N + 1}, inf_v<ll>);
    dp[0][0]  = 0;
    for (int np = 0; np <= N; np++) {
        for (int p = 0; p < np; p++) {
            for (int pp = 0; pp <= p; pp++) {
                const ll fore = fsum(pp, p, np);
                const ll back = bsum(pp, p, np);
                chmin(dp[p][np], fore + back + dp[pp][p]);
            }
        }
    }
    ll ans = inf_v<ll>;
    for (int p = 0; p <= N; p++) { chmin(ans, dp[p][N]); }
    outln(ans + btotal);
    return 0;
}

