int dp1(int i, int j, int kind){
    if(i > j)return 0;
    if(kind == -1)return j-i+1;
    if(DP1C[i][j][kind])return DP1[i][j][kind];
    int ans = dp1(i,j-1,kind) + dp1(j,n-1,kind-1);
    DP1[i][j][kind] = ans;
    DP1C[i][j][kind] = 1;
    return ans;
}

int enumerate(int a, int b, int c){
    return dp1(0, a-1, 1) + dp1(a, b-1, 0) + dp1(b, c-1, -1);
}

