    int h = enumerate(a,b,c);
    if(DPC[h])return DP[h];
    ll ans1 = A[a][b] + B[a][c] + dp(a-1,b,c);
    ll ans2 = big*big;
    if(b > a+1)ans2 = A[a][a+1] + B[a][b-1] + dp(a-1,a+1,b-1);
    ll ans3 = B[a][a] + dp(a-1, a, a);

    ll ans = ans1;
    ll pek = 0;
    if(ans2 < ans){
        ans = ans2;
        pek = 1;
    }
    if(ans3 < ans){
        ans = ans3;
