#define ll long long

const ll llf = (signed ll) (~0ull >> 2);

const int N = 505;

int n;
int A[N][N];
ll s[N][N];
ll dp[N][N];

int main() {
	scanf("%d", &n);
	for (int i = 1; i <= n; i++) {
		for (int j = 1; j <= n; j++) {
			if (i == j) {
				continue;
			}
			scanf("%d", A[i] + j);
			s[i][j] = A[i][j] + s[i - 1][j] + s[i][j - 1] - s[i - 1][j - 1];
		}
	}
	for (int i = 1; i <= n; i++)
		for (int j = 1; j <= n; j++)
			dp[i][j] = llf;
	dp[1][1] = 0;
	for (int i = 2; i <= n; i++) {
		dp[1][i] = dp[1][i - 1];
		for (int j = 1; j < i; j++) {
			dp[1][i] += A[j][i];
		}
	}
	for (int i = 2; i <= n; i++) {
		ll cost = 0;
		for (int b = i - 1; b; b--) {
			for (int j = b + 2; j <= i; j++)
				cost += A[b + 1][j];
			for (int a = b; a; a--) {
				dp[b + 1][i] = min(dp[b + 1][i], dp[a][b] + cost + s[i][a - 1] - s[b][a - 1]);
			}
		}
	}
	ll ans = llf;
	for (int i = 1; i <= n; i++)
		ans = min(ans, dp[i][n]);
	printf("%lld\n", ans);	
	return 0;
}

#include <bits/stdc++.h>
using namespace std;

typedef long long ll;

const int Maxn = 505;

int n;
ll sumA[Maxn][Maxn];
ll all;
ll A[Maxn][Maxn], B[Maxn][Maxn], C[Maxn][Maxn];
ll rem;

ll Get(int row, int c1, int c2)
{
    return c1 > c2? 0: sumA[row][c2] - sumA[row][c1 - 1];
}

int main()
{
    scanf("%d", &n);
    for (int i = 1; i <= n; i++)
        for (int j = 1; j <= n; j++) {
            int a;
            if (i == j) a = 0;
            else scanf("%d", &a);
            sumA[i][j] = a + sumA[i][j - 1];
            all += a;
        }
    for (int i = 1; i <= n; i++)
        rem += Get(i, 1, i - 1);
    for (int i = 1; i <= n; i++)
        for (int j = i + 1; j <= n; j++) {
            for (int k = 1; k <= i; k++)
                A[i][j] += Get(k, j, n);
            for (int k = i + 1; k < j; k++)
                A[i][j] += Get(k, j + 1, n);
        }
    for (int i = 1; i <= n; i++)
        for (int j = i; j <= n; j++) {
            for (int k = i; k < j; k++)
                B[i][j] += Get(k, j, n);
        }
    for (int i = 1; i <= n; i++)
        for (int j = i + 1; j <= n; j++) {
            for (int k = 1; k < j; k++)
                C[i][j] += Get(k, 1, k - 1);
            for (int k = j; k <= n; k++)
                C[i][j] += Get(k, i + 1, k - 1);
        }
    ll mx = 0;
    for (int i = 1; i <= n; i++)
        for (int j = i + 1; j <= n; j++) {
            mx = max(mx, A[i][j] + rem);
            for (int k = j + 1; k <= n; k++)
                mx = max(mx, A[i][j] + B[j][k] + C[i][k]);
        }
    cout << all - mx << endl;
    return 0;
}

